E_D("replace into `wb_feed_node` values('1','public','post','原创微博','&lt;title comment=&quot;feed标题&quot;&gt; \r\n			&lt;![CDATA[ {\$actor}  ]]&gt;\r\n		&lt;/title&gt;\r\n		&lt;body comment=&quot;feed详细内容/引用的内容&quot;&gt;&lt;a href=&quot;&quot;&gt;详情&lt;/a&gt;\r\n			&lt;![CDATA[{\$body} ]]&gt;&lt;a href=&quot;&quot;&gt;详情&lt;/a&gt;\r\n		&lt;/body&gt;\r\n		&lt;feedAttr  comment=&quot;true&quot; repost=&quot;true&quot; favor=&quot;true&quot;  delete=&quot;true&quot; /&gt;');");
