n				&lt;a href=&quot;{:U(''public/Profile/feed'',array(''uid''=&gt;\$sourceInfo[''uid''],''feed_id''=&gt;\$sourceInfo[''feed_id'']))}&quot;&gt;原文评论&lt;neq name=&quot;sourceInfo.comment_count&quot; value=&quot;0&quot;&gt;({\$sourceInfo.comment_count})&lt;/neq&gt;&lt;/a&gt;\r\n			&lt;/span&gt;\r\n			&lt;span&gt;&lt;a href=&quot;{:U(''public/Profile/feed'',array(''uid''=&gt;\$sourceInfo[''uid''],''feed_id''=&gt;\$sourceInfo[''feed_id'']))}&quot; class=&quot;date&quot;&gt;{\$sourceInfo[''ctime'']|friendlyDate}&lt;/a&gt;&lt;span&gt;来自网站&lt;/span&gt;&lt;/span&gt;\r\n		&lt;/p&gt;\r\n--&gt;\r\n		&lt;php&gt;else:&lt;/php&gt;\r\n		&lt;dd class=&quot;name&quot;&gt;内容已被删除&lt;/dd&gt;\r\n		&lt;php&gt;endif;&lt;/php&gt;\r\n	&lt;/dl&gt;\r\n	]]&gt;\r\n&lt;/body&gt;\r\n&lt;feedAttr  comment=&quot;true&quot; repost=&quot;true&quot; favor=&quot;true&quot;  delete=&quot;true&quot; /&gt;');");
E_D("replace into `wb_feed_node` values('6','weiba','weiba_repost','微吧转发','	&lt;title&gt; \r\n			&lt;![CDATA[{\$actor} ]]&gt;\r\n		&lt;/title&gt;\r\n		&lt;body&gt;\r\n			&lt;![CDATA[\r\n				&lt;eq name=''body'' value=''''&gt; 微博分享 &lt;/eq&gt; \r\n				{\$body}\r\n				&lt;dl class=&quot;comment&quot;&gt;&lt;dt class=&quot;arrow bgcolor_arrow&quot;&gt;&lt;em class=&quot;arrline&quot;&gt;◆&lt;/em&gt;&lt;span class=&quot;downline&quot;&gt;◆&lt;/span&gt;&lt;/dt&gt;\r\n				&lt;dd class=&quot;name&quot;&gt;\r\n						&lt;php&gt; if(\$sourceInfo[''source_user_info''][''uid''] == \$GLOBALS[''ts''][''mid'']){&lt;/php&gt;\r\n					@{\$sourceInfo.source_user_info.uname}\r\n					&lt;php&gt; }else{&lt;/php&gt;\r\n					@{\$sourceInfo.source_user_info.uname}\r\n					&lt;php&gt; }&lt;/php&gt;\r\n&lt;volist name=&quot;sourceInfo[''groupData''][\$sourceInfo[''source_user_info''][''uid'']]&quot; id=&quot;v2&quot;&gt;\r\n        				&lt;img style=&quot;width:auto;height:auto;display:inline;cursor:pointer&quot; src=&quot;{\$v2[''user_group_icon_url'']}&quot; title=&quot;{\$v2[''user_group_name'']}&quot; /&gt; \r\n    				&lt;/volist&gt;\r\n				&lt;/dd&gt;\r\n				&lt;dd&gt;\r\n					{\$sourceInfo.source_content}\r\n&lt;php&gt;if(APP_NAME == ''public'' || APP_NAME == ''widget''){&lt;/php&gt;\r\n&lt;a href=&quot;javascript:void(0)&quot; class=&quot;ico-details&quot; event-node =''loadPost'' event-args=''feed_id={\$feedid}&amp;post_id={\$app_row_id}'' id=&quot;{\$app_row_id}&quot;&gt;&lt;i class=&quot;triangle-l&quot;&gt;&lt;/i&gt;查看全文&lt;/a&gt;&lt;div class=&quot;feed_img_lists&quot; style=&quot;display:none;&quot; id=&quot;post_{\$feedid}_{\$app_row_id}&quot;&gt;\r\n&lt;/div&gt;\r\n&lt;php&gt;}else{&lt;/php&gt;\r\n&lt;a href=&quot;{:U(''weiba/Index/postDetail'',array(''post_id''=&gt;\$app_row_id))}&quot; class=&quot;ico-details&quot; target=&quot;_blank&quot;&gt;&lt;/a&gt;\r\n&lt;php&gt;}&lt;/php&gt;\r\n					&lt;php&gt;if(!empty(\$sourceInfo[''attach''])):&lt;/php&gt;\r\n					\r\n					&lt;eq n